    def wheelEvent(self, event):
        if event.modifiers()==QtCore.Qt.CTRL:
            if event.delta()>0:
                self.viewZoomIn()
                event.ignore()
            else:
                self.viewZoomOut()
                event.ignore()
        
        
        
