def getFileName(pathName, separatorSymbol=None):
    #print pathName
    if separatorSymbol:
        try:
            return pathName.split(separatorSymbol)[-1]
        except AttributeError:
            return None
    elif ( '\\' in pathName ) :
        return pathName.split('\\')[-1]
    else:
        return pathName.split('/')[-1]


